function y = highPassChebyshev1Filt_apnea(x, fs)
%DOFILTER Filters input x and returns output y.

% MATLAB Code
% Generated by MATLAB(R) 9.3 and DSP System Toolbox 9.5.
% Generated on: 02-Oct-2017 12:46:13

persistent Hd;

if isempty(Hd)
    
    Fstop = 0.01;  % Stopband Frequency
    Fpass = 1;     % Passband Frequency
    Astop = 10;    % Stopband Attenuation (dB)
    Apass = 2;     % Passband Ripple (dB)
    Fs    = fs;   % Sampling Frequency
    
    h = fdesign.highpass('fst,fp,ast,ap', Fstop, Fpass, Astop, Apass, Fs);
    
    Hd = design(h, 'cheby1', ...
        'MatchExactly', 'passband'); % SSScaleNorm was set to Linf prior to downloading Matlab 2020a
    
    
    
    set(Hd,'PersistentMemory',true);
    
end

y = filter(Hd,x);
